namespace frontend\controllers;

use common\helpers\AlarmHelper;
use common\helpers\Excel;
use common\services\FuncOverview;
use Yii;

/**
 * 产品分析总榜
 * @package frontend\controllers
 */
class FuncOverviewController extends JzBaseController
{
    public $cache_action_id = [''index''];

    public function init()
    {
        parent::init();
        $this->on(self::EXCEL, [$this, ''exportExcel'']);
    }

    /**
     * 导出excel
     * @param \yii\base\Event $event
     * @throws \PhpOffice\PhpSpreadsheet\Exception
     */
    public function exportExcel(\yii\base\Event $event)
    {
        //dd(Yii::$app->response->data);
        if(Yii::$app->response->data[''code''] == self::CODE_SUCCESS) {
            $excel = new Excel();
            $excel->setColumnNames([''名称'', ''点击量'', ''用户量'']);
            $data = (Yii::$app->response->data[''data''] instanceof \stdClass) ? [] : Yii::$app->response->data[''data''][''list''];
            $excel->addRows($data, [''event_name'', ''click_num'', ''user_num'']);
            $excel->export(''产品分析-总榜'');
        }
    }

    /** 产品分析总榜接口
     * @return \yii\console\Response|\yii\web\Response
     */
    public function actionIndex()
    {
        $request = Yii::$app->request;
        if (!$request->isGet) {
            return $this->renderJson(self::CODE_ERROR, ''invalid request'');
        }
        try{
            //必传
            $start_date = $request->get(''start_date'', '''');
            $end_date = $request->get(''end_date'', '''');
            $app_id  = $request->get(''app_id'', '''');
            $terminal_key = $request->get(''terminal_key'', '''');

            if(!strlen($start_date)) {
                return $this->renderJson(self::CODE_ERROR, ''invalid start_date'');
            }

            if(!strlen($end_date)) {
                return $this->renderJson(self::CODE_ERROR, ''invalid end_date'');
            }

            if(!strlen($app_id)) {
                return $this->renderJson(self::CODE_ERROR, ''invalid app_id'');
            }

            if(!strlen($terminal_key)) {
                return $this->renderJson(self::CODE_ERROR, ''invalid terminal_key'');
            }

            //选传
            $param = [
                ''sort_field''=>Yii::$app->request->get(''sort_field'', ''click_num''), // click_num | user_num
                ''sort_type''=>Yii::$app->request->get(''sort_type'', ''desc''),
                ''version_key''=> (array) $request->get(''version_key'', []),
                ''user_type_key'' => $request->get(''user_type_key'', ''quanbu''),
                ''platform_key'' => intval($request->get(''platform_key'', -1)),
                ''keyword''=>$request->get(''keyword'', ''''),
                ''page_size''=>$request->get(''page_size'', 20),
                ''page''=>$request->get(''page'', 1),
            ];

            $result = FuncOverview::search($start_date, $end_date, $app_id, $terminal_key, $param);

            return $this->renderJson(self::CODE_SUCCESS, ''success'', $result);
        }catch (\Exception $exception) {
            $message = sprintf(''产品分析总榜接口失败: 【%s】%s（%s第%d行）'', YII_ENV, $exception->getMessage(), $exception->getFile(), $exception->getLine());
            AlarmHelper::send(''text'', [
                ''content''=>$message,
            ]);
            return $this->renderJson(self::CODE_ERROR, $message);
        }
    }
}
